mapper.select:
select A.iboard_cmt iboardCmt,A.iboard,A.iuser ,B.nm writer,B.main_pic writerMainPic,A.ctnt ,A.created_at createdAt
from t_board_cmt A inner join t_user B on A.iuser=B.iuser
where A.iboard=#{iboard} order by A.iboard_cmt limit #{stidx},#{row}

select iboard_cmt
from t_board_cmt
where iuser=#{iuser} and iboard=#{iboard}

select count(iboard_cmt)
from t_board_cmt
where iboard =#{iboard}

mapper.delete:
delete
from t_board_cmt


iboard_cmt = #{iboardCmt}
        and iuser = #{iuser}


    iboard=#{iboard}
    and iuser = #{iuser}

mapper.update:
update t_board_cmt
set ctnt=#{ctnt}
,updated_at=now()
where iboard_cmt=#{iboardCmt} and iuser =#{iuser}
